INSERT INTO public.site_settings (key, value, type, category, label, description) VALUES
('legal_privacy_section2_title', 'Verantwortliche Stelle', 'text', 'legal', 'Datenschutz Sektion 2 Titel', 'Titel für Verantwortliche Stelle'),
('legal_privacy_section2_content', 'Die verantwortliche Stelle für die Datenverarbeitung auf dieser Website ist der Betreiber dieser Plattform. Die verantwortliche Stelle entscheidet allein oder gemeinsam mit anderen über die Zwecke und Mittel der Verarbeitung von personenbezogenen Daten.', 'textarea', 'legal', 'Datenschutz Sektion 2 Inhalt', 'Inhalt für Verantwortliche Stelle'),
('legal_privacy_section3_title', 'Erhebung und Speicherung personenbezogener Daten', 'text', 'legal', 'Datenschutz Sektion 3 Titel', 'Titel für Datenerhebung'),
('legal_privacy_section3_content', 'Bei der Nutzung unserer Plattform erheben wir verschiedene personenbezogene Daten. Personenbezogene Daten sind Daten, mit denen Sie persönlich identifiziert werden können.', 'textarea', 'legal', 'Datenschutz Sektion 3 Inhalt', 'Inhalt für Datenerhebung'),
('legal_privacy_section4_title', 'Registrierungsdaten', 'text', 'legal', 'Datenschutz Sektion 4 Titel', 'Titel für Registrierungsdaten'),
('legal_privacy_section4_content', 'Bei der Registrierung auf unserer Plattform werden folgende Daten erhoben: E-Mail-Adresse, Passwort (verschlüsselt gespeichert), Zeitpunkt der Registrierung. Diese Daten sind für die Nutzung unserer Dienste erforderlich.', 'textarea', 'legal', 'Datenschutz Sektion 4 Inhalt', 'Inhalt für Registrierungsdaten'),
('legal_privacy_section5_title', 'Profildaten', 'text', 'legal', 'Datenschutz Sektion 5 Titel', 'Titel für Profildaten'),
('legal_privacy_section5_content', 'Wenn Sie ein Profil erstellen, können Sie freiwillig weitere Daten angeben: Anzeigename, Alter, Standort (Kanton, Stadt), Beschreibung, Fotos, Kontaktdaten (Telefon, E-Mail, Website, Social Media). Diese Daten werden öffentlich auf der Plattform angezeigt.', 'textarea', 'legal', 'Datenschutz Sektion 5 Inhalt', 'Inhalt für Profildaten'),
('legal_privacy_section6_title', 'Nutzungsdaten', 'text', 'legal', 'Datenschutz Sektion 6 Titel', 'Titel für Nutzungsdaten'),
('legal_privacy_section6_content', 'Bei der Nutzung unserer Plattform werden automatisch technische Daten erfasst: IP-Adresse (anonymisiert), Browser-Typ und -Version, Betriebssystem, Referrer URL, Zeitpunkt des Zugriffs. Diese Daten dienen der Sicherheit und Optimierung unserer Dienste.', 'textarea', 'legal', 'Datenschutz Sektion 6 Inhalt', 'Inhalt für Nutzungsdaten'),
('legal_privacy_section7_title', 'Cookies', 'text', 'legal', 'Datenschutz Sektion 7 Titel', 'Titel für Cookies'),
('legal_privacy_section7_content', 'Unsere Website verwendet Cookies. Cookies sind kleine Textdateien, die auf Ihrem Endgerät gespeichert werden. Wir verwenden nur technisch notwendige Cookies für die Authentifizierung und Session-Verwaltung. Sie können Cookies in Ihren Browser-Einstellungen deaktivieren.', 'textarea', 'legal', 'Datenschutz Sektion 7 Inhalt', 'Inhalt für Cookies'),
('legal_privacy_section8_title', 'Weitergabe von Daten', 'text', 'legal', 'Datenschutz Sektion 8 Titel', 'Titel für Datenweitergabe'),
('legal_privacy_section8_content', 'Eine Weitergabe Ihrer personenbezogenen Daten an Dritte erfolgt nur, wenn dies zur Vertragserfüllung erforderlich ist, Sie ausdrücklich eingewilligt haben, oder eine gesetzliche Verpflichtung besteht. Ihre Profildaten sind öffentlich einsehbar, sofern Sie ein Profil erstellen.', 'textarea', 'legal', 'Datenschutz Sektion 8 Inhalt', 'Inhalt für Datenweitergabe'),
('legal_privacy_section9_title', 'Ihre Rechte', 'text', 'legal', 'Datenschutz Sektion 9 Titel', 'Titel für Benutzerrechte'),
('legal_privacy_section9_content', 'Sie haben folgende Rechte bezüglich Ihrer Daten: Auskunft über gespeicherte Daten, Berichtigung unrichtiger Daten, Löschung Ihrer Daten, Einschränkung der Verarbeitung, Datenübertragbarkeit, Widerspruch gegen die Verarbeitung. Kontaktieren Sie uns für die Ausübung dieser Rechte.', 'textarea', 'legal', 'Datenschutz Sektion 9 Inhalt', 'Inhalt für Benutzerrechte'),
('legal_privacy_section10_title', 'Datensicherheit', 'text', 'legal', 'Datenschutz Sektion 10 Titel', 'Titel für Datensicherheit'),
('legal_privacy_section10_content', 'Wir setzen technische und organisatorische Sicherheitsmassnahmen ein: SSL/TLS-Verschlüsselung, sichere Passwort-Speicherung (bcrypt), Zugangskontrolle, regelmässige Sicherheitsupdates. Trotz aller Massnahmen kann keine absolute Sicherheit garantiert werden.', 'textarea', 'legal', 'Datenschutz Sektion 10 Inhalt', 'Inhalt für Datensicherheit'),
('legal_privacy_section11_title', 'Speicherdauer', 'text', 'legal', 'Datenschutz Sektion 11 Titel', 'Titel für Speicherdauer'),
('legal_privacy_section11_content', 'Ihre Daten werden so lange gespeichert, wie Ihr Konto aktiv ist. Bei Kontolöschung werden alle personenbezogenen Daten innerhalb von 30 Tagen gelöscht, sofern keine gesetzlichen Aufbewahrungspflichten bestehen.', 'textarea', 'legal', 'Datenschutz Sektion 11 Inhalt', 'Inhalt für Speicherdauer'),
('legal_privacy_section12_title', 'Änderungen dieser Datenschutzerklärung', 'text', 'legal', 'Datenschutz Sektion 12 Titel', 'Titel für Änderungen'),
('legal_privacy_section12_content', 'Wir behalten uns vor, diese Datenschutzerklärung anzupassen, um sie an geänderte Rechtslagen oder Änderungen unserer Dienste anzupassen. Die aktuelle Version ist stets auf unserer Website verfügbar.', 'textarea', 'legal', 'Datenschutz Sektion 12 Inhalt', 'Inhalt für Änderungen'),
